package kondisi;

public class kondisiIfElse {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int i = 30;
	      
		if (i > 40)
		   {      
		     System.out.println(i > 40);
		    }
		 else
		    {
		      System.out.println(i > 40);
		     }
		           
		 System.out.println("ini sudah diluar if..else");

	}

}
