package perulangan;

public class perulanganDoWhile {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
	     int angka = 12;
	        System.out.println("Pernyataan do...while");
	        System.out.println("---------------------");
	        do
	        {
	            System.out.println("Nilai angka  :   " + angka);
	            angka++;
	        }
	        while (angka < 20);

	}

}
